class Csalaespera extends CI_Controller {

    public function __construct() {
        parent::__construct();
        $this->load->helper('form');
        $this->load->helper('url');
        $this->load->helper('security');
        $this->load->model("oficina/salaespera/Msalaespera");
        $this->load->model("dominio/Mcda");
        $this->load->model("dominio/Mhojatrabajo");
        $this->load->model("Mutilitarios");
        $this->load->library('Opensslencryptdecrypt');
    }

    public function index() {
        $encrptopenssl = New Opensslencryptdecrypt();
        $json = $encrptopenssl->decrypt(file_get_contents('system/oficina.json', true), true);
        $ofc = json_decode($json, true);
        $salaEspera = false;
        $deco = '0';
        $leyenda = "Bienvenido a nuestro CDA";
        foreach ($ofc as $d) {
            if ($d['nombre'] == 'salaEspera' && $d['valor'] == '1') {
                $salaEspera = true;
            }
            if ($d['nombre'] == 'deco' && $d['valor'] == '1') {
                $deco = '1';
            }
        }
        if (!$salaEspera) {
            redirect('Cindex');
        } else {
            $data = $this->getDatos();
            $data['deco'] = $deco;
            $data['leyenda'] = file_get_contents('./leyenda.txt', true);
            $this->load->view('oficina/salaespera/Vsalaespera', $data);
        }
    }

    public function getCda() {
        $result = $this->Mcda->get();
        if ($result->num_rows() > 0) {
            $r = $result->result();
            return $r[0];
        } else {
            return 'NA';
        }
    }

    public function llamar() {
        $result = $this->Msalaespera->llamar();
        if ($result->num_rows() > 0) {
            $r = $result->result();
            $this->Cllamar($r[0]->idhojapruebas);
            echo $r[0]->idservicio . "|" . $r[0]->numero_placa;
        } else {
            echo 'NA';
        }
    }

    public function Cllamar($idhojapruebas) {
        $datHT['llamar'] = "2";
        $datHT['idhojapruebas'] = $idhojapruebas;
        $this->Mhojatrabajo->update_llamar($datHT);
    }

