<?php

class MyTCPDF extends TCPDF {

    var $htmlHeader;

    public function setHtmlHeader($htmlHeader, $logo) {
        $cdaName = "CARDISEL";
        $pos = strpos($htmlHeader, $cdaName);
        if (!$pos) {
            $html = <<<EOF
                <table>
                    <tr>
                        <td width="160px">
                            <img src="@$logo" style="width: 150px;height: 75px" />
                        </td>
                        <td width="340px">
                            $htmlHeader
                        </td>
                        <td>
                            <br><br>
                            <img src="super.jpg" style="width: 130px" />
                        </td>
                    </tr>
                </table>
                <hr>
EOF;
        } else {
            $html = <<<EOF
                <table>
                    <tr>
                        <td border="1" width="160px" align="center"><br><br>
                            <img style="width: 140px;height: 50px" src="@" />
                        </td>
                        <td border="1" width="300px" align="center" >
                            <strong>RECEPCIÓN Y ENTREGA DEL VEHÍCULO</strong><br>
                            <img style="width: 140px;height: 50px" src="@" />
                        </td>
                        <td width="58px" style="border-top-width:0.1px;" >
                            Código:
                            <hr>
                            Versión:
                            <hr>
                            Elaboró:
                            <hr>
                            Revisó:
                            <hr>
                            Aprobó:
                        </td>
                        <td width="120px" style="border-right-width:0.1px;border-top-width:0.1px;">
                            GO-FM-004
                            <hr>
                            7-21/09/2020
                            <hr>
                            <label style="color: gray">Jennifer Díaz</label>
                            <hr>
                            <label style="color: gray">Johana Villamar</label>
                            <hr>
                            <label style="color: gray">José M. Ferreira</label>
                        </td>
                    </tr>
                </table>
                <hr>
EOF;
        }


        $this->htmlHeader = $html;
    }

    public function Header() {
        $this->writeHTMLCell(
                $w = 0, $h = 0, $x = '', $y = '', $this->htmlHeader, $border = 0, $ln = 1, $fill = 0, $reseth = true, $align = 'top', $autopadding = true);
    }

}

